(1, 1, 0, 'lowercaseUrl', 'Для передресации сылок с прописными буквами на строчные', 0, 8, 0, '$eventName = $modx->event->name;\n\nswitch($eventName) {\n    case ''OnHandleRequest'':\n        if($modx->context->get(''key'') != "mgr"){\n            if(isset($_GET[''rewrite-strtolower-url''])) {\n                $url = $_GET[''rewrite-strtolower-url''];\n                unset($_GET[''rewrite-strtolower-url'']);\n                $params = http_build_query($_GET);\n                if(strlen($params)) {\n                    $params = ''?'' . $params;\n                }\n                $modx->sendRedirect(''http://'' . $_SERVER[''HTTP_HOST''] . ''/'' . strtolower($url) . $params, array(''responseCode'' => ''HTTP/1.1 301 Moved Permanently''));\n            }\n        }\n        break;\n}', 0, 'a:0:{}', 0, '', 0, ''),
(2, 1, 0, 'transferProtocol', 'Для передресации по нужному протоколу http или https исходя из настроек', 0, 8, 0, '$eventName = $modx->event->name;\n\nswitch($eventName) {\n    case ''OnHandleRequest'':\n        $isSecure = false;\n        if (isset($_SERVER[''HTTPS'']) && $_SERVER[''HTTPS''] == ''on'') {\n            $isSecure = true;\n        } elseif (!empty($_SERVER[''HTTP_X_FORWARDED_PROTO'']) && $_SERVER[''HTTP_X_FORWARDED_PROTO''] == ''https'' || !empty($_SERVER[''HTTP_X_FORWARDED_SSL'']) && $_SERVER[''HTTP_X_FORWARDED_SSL''] == ''on'') {\n            $isSecure = true;\n        }\n\n        $REQUEST_PROTOCOL = $isSecure ? ''https'' : ''http'';\n        $SYSTEM_PROTOCOL = $modx->getOption(''server_protocol'');\n        if($SYSTEM_PROTOCOL != $REQUEST_PROTOCOL) {\n            $url =  str_replace(array("http://","https://"), "", $_SERVER[''HTTP_HOST''] . $_SERVER[''REQUEST_URI'']);\n            if($SYSTEM_PROTOCOL == "https")\n                $modx->sendRedirect(''https://'' . $_SERVER[''HTTP_HOST''] . $_SERVER[''REQUEST_URI''], array(''responseCode'' => ''HTTP/1.1 301 Moved Permanently''));\n            else\n                $modx->sendRedirect(''http://'' . $_SERVER[''HTTP_HOST''] . $_SERVER[''REQUEST_URI''], array(''responseCode'' => ''HTTP/1.1 301 Moved Permanently''));\n            header("Location:$redirect");\n        }\n        break;\n}', 0, 'a:0:{}', 0, '', 0, ''),
(3, 1, 0, 'pdoTools', '', 0, 9, 0, '/** @var modX $modx */\nswitch ($modx->event->name) {\n\n    case ''OnMODXInit'':\n        $fqn = $modx->getOption(''pdoTools.class'', null, ''pdotools.pdotools'', true);\n        $path = $modx->getOption(''pdotools_class_path'', null, MODX_CORE_PATH . ''components/pdotools/model/'', true);\n        $modx->loadClass($fqn, $path, false, true);\n\n        $fqn = $modx->getOption(''pdoFetch.class'', null, ''pdotools.pdofetch'', true);\n        $path = $modx->getOption(''pdofetch_class_path'', null, MODX_CORE_PATH . ''components/pdotools/model/'', true);\n        $modx->loadClass($fqn, $path, false, true);\n        break;\n\n    case ''OnSiteRefresh'':\n        /** @var pdoTools $pdoTools */\n        if ($pdoTools = $modx->getService(''pdoTools'')) {\n            if ($pdoTools->clearFileCache()) {\n                $modx->log(modX::LOG_LEVEL_INFO, $modx->lexicon(''refresh_default'') . '': pdoTools'');\n            }\n        }\n        break;\n}', 0, NULL, 0, '', 0, 'core/components/pdotools/elements/plugins/plugin.pdotools.php'),
(4, 0, 0, 'phpThumbOfCacheManager', 'Handles cache cleaning when clearing the Site Cache.', 0, 10, 0, '/*\n * Handles cache cleanup\n * pThumb\n * Copyright 2013 Jason Grant\n *\n * Please see the GitHub page for documentation or to report bugs:\n * https://github.com/oo12/phpThumbOf\n *\n * pThumb is free software; you can redistribute it and/or modify it\n * under the terms of the GNU General Public License as published by the Free\n * Software Foundation; either version 2 of the License, or (at your option) any\n * later version.\n *\n * pThumb is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * phpThumbOf; if not, write to the Free Software Foundation, Inc., 59 Temple\n * Place, Suite 330, Boston, MA 02111-1307 USA\n */\n\nif ($modx->event->name === ''OnSiteRefresh'') {\n	if (!$modx->loadClass(''pThumbCacheCleaner'', MODX_CORE_PATH . ''components/phpthumbof/model/'', true, true)) {\n		$modx->log(modX::LOG_LEVEL_ERROR, ''[pThumb] Could not load pThumbCacheCleaner class.'');\n		return;\n	}\n	static $pt_settings = array();\n	$pThumb = new pThumbCacheCleaner($modx, $pt_settings, array(), true);\n	$pThumb->cleanCache();\n}', 0, NULL, 0, '', 0, ''),
(5, 1, 0, 'Tickets', '', 0, 12, 0, 'switch ($modx->event->name) {\n\n	case ''OnSiteRefresh'':\n		if ($modx->cacheManager->refresh(array(''default/tickets'' => array()))) {\n			$modx->log(modX::LOG_LEVEL_INFO, $modx->lexicon(''refresh_default'') . '': Tickets'');\n		}\n		break;\n\n\n	case ''OnDocFormSave'':\n		/* @var Ticket $resource */\n		if ($mode == ''new'' && $resource->class_key == "Ticket") {\n			$modx->cacheManager->delete(''tickets/latest.tickets'');\n		}\n		/* @var TicketsSection $resource */\n		if ($mode == ''upd'' && $resource->class_key == ''TicketsSection'') {\n			if (method_exists($resource, ''clearCache'')) {\n				$resource->clearCache();\n			}\n		}\n		break;\n\n\n	case ''OnWebPagePrerender'':\n		$output = &$modx->resource->_output;\n		$output = str_replace(\n			array(''*(*(*(*(*(*'', ''*)*)*)*)*)*'', ''~(~(~(~(~(~'', ''~)~)~)~)~)~''),\n			array(''['', '']'', ''{'', ''}''),\n			$output\n		);\n		break;\n\n\n	case ''OnPageNotFound'':\n		// It is working only with friendly urls enabled\n		$q = trim(@$_REQUEST[$modx->context->getOption(''request_param_alias'', ''q'')]);\n		$matches = explode(''/'', rtrim($q, ''/''));\n		if (count($matches) < 2) {\n			return;\n		}\n\n		$ticket_uri = array_pop($matches);\n		$section_uri = implode(''/'', $matches) . ''/'';\n\n		if ($section_id = $modx->findResource($section_uri)) {\n			/** @var TicketsSection $section */\n			if ($section = $modx->getObject(''TicketsSection'', $section_id)) {\n				if (is_numeric($ticket_uri)) {\n					$ticket_id = $ticket_uri;\n				}\n				elseif (preg_match(''#^\\d+#'', $ticket_uri, $tmp)) {\n					$ticket_id = $tmp[0];\n				}\n				else {\n					$properties = $section->getProperties(''tickets'');\n					if (!empty($properties[''uri'']) && strpos($properties[''uri''], ''%id'') !== false) {\n						$pcre = str_replace(''%id'', ''([0-9]+)'', $properties[''uri'']);\n						$pcre = preg_replace(''#(\\%[a-z]+)#'', ''(?:.*?)'', $pcre);\n						if (preg_match($pcre, $ticket_uri, $matches)) {\n							$ticket_id = $matches[1];\n						}\n					}\n				}\n				if (!empty($ticket_id)) {\n					if ($ticket = $modx->getObject(''Ticket'', array(''id'' => $ticket_id, ''deleted'' => 0))) {\n						if ($ticket->published) {\n							$modx->sendRedirect($modx->makeUrl($ticket_id), array(''responseCode'' => ''HTTP/1.1 301 Moved Permanently''));\n						}\n						elseif ($unp_id = $modx->getOption(''tickets.unpublished_ticket_page'')) {\n							$modx->sendForward($unp_id);\n						}\n					}\n				}\n			}\n		}\n		break;\n\n\n	case ''OnLoadWebDocument'':\n		$authenticated = $modx->user->isAuthenticated($modx->context->get(''key''));\n		$key = ''Tickets_User'';\n\n		if (!$authenticated && !$modx->getOption(''tickets.count_guests'')) {\n			return;\n		}\n\n		if (empty($_COOKIE[$key])) {\n			if (!empty($_SESSION[$key])) {\n				$guest_key = $_SESSION[$key];\n			}\n			else {\n				$guest_key = $_SESSION[$key] = md5(rand() . time() . rand());\n			}\n			setcookie($key, $guest_key, time() + (86400 * 365), ''/'');\n		}\n		else {\n			$guest_key = $_COOKIE[$key];\n		}\n\n		if (empty($_SESSION[$key])) {\n			$_SESSION[$key] = $guest_key;\n		}\n\n		if ($authenticated) {\n			/** @var TicketAuthor $profile */\n			if (!$profile = $modx->user->getOne(''AuthorProfile'')) {\n				$profile = $modx->newObject(''TicketAuthor'');\n				$modx->user->addOne($profile);\n			}\n			$profile->set(''visitedon'', time());\n			$profile->save();\n		}\n		break;\n\n\n	case ''OnWebPageComplete'':\n		/** @var Tickets $Tickets */\n		$Tickets = $modx->getService(''tickets'');\n		$Tickets->logView($modx->resource->get(''id''));\n		break;\n\n\n	case ''OnUserSave'':\n		/** @var modUser $user */\n		if ($mode == ''new'' && $user && !$user->getOne(''AuthorProfile'')) {\n			$profile = $modx->newObject(''TicketAuthor'');\n			$user->addOne($profile);\n			$profile->save();\n		}\n		break;\n\n}', 0, NULL, 0, '', 0, 'core/components/tickets/elements/plugins/plugin.tickets.php'),
(6, 0, 0, 'QuipResourceCleaner', 'Cleans up threads when a Resource is removed.', 0, 0, 0, '/**\n * Quip\n *\n * Copyright 2010-11 by Shaun McCormick <shaun@modx.com>\n *\n * This file is part of Quip, a simple commenting component for MODx Revolution.\n *\n * Quip is free software; you can redistribute it and/or modify it under the\n * terms of the GNU General Public License as published by the Free Software\n * Foundation; either version 2 of the License, or (at your option) any later\n * version.\n *\n * Quip is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * Quip; if not, write to the Free Software Foundation, Inc., 59 Temple Place,\n * Suite 330, Boston, MA 02111-1307 USA\n *\n * @package quip\n */\n/**\n * Handles removal of threads if a Resource is deleted.\n * \n * @package quip\n */\n$quip = $modx->getService(''quip'',''Quip'',$modx->getOption(''quip.core_path'',null,$modx->getOption(''core_path'').''components/quip/'').''model/quip/'',$scriptProperties);\nif (!($quip instanceof Quip)) return;\n\nswitch ($modx->event->name) {\n    case ''OnEmptyTrash'':\n        foreach ($scriptProperties[''ids''] as $id) {\n            if (empty($id)) continue;\n\n            $threads = $modx->getCollection(''quipThread'',array(''resource'' => $id));\n            foreach ($threads as $thread) {\n                $modx->log(modX::LOG_LEVEL_INFO,''[Quip] Removing thread: ''.$thread->get(''name''));\n                $thread->remove();\n            }\n        }\n        break;\n}\nreturn;', 0, NULL, 0, '', 0, ''),
